	(32, 'trigger:magmadar lava bomb', 1, 'magmadar lava bomb [h:object|trigger] [c:magmadar lava bomb|trigger now]\r\n\r\nReaction behavior:\r\nExecutes: [h:action|move away from magmadar lava bomb] (100.000) for [h:strategy|magmadar]', '', '', '', '', '', '', '', '', '', 0),
	(33, 'trigger:ah bid', 1, 'ah bid [h:object|trigger] [c:ah bid|trigger now]\r\n\r\nCombat behavior:\r\nExecutes: [h:action|ah bid] (100.000) for [h:strategy|chat]\r\nNon combat behavior:\r\nExecutes: [h:action|ah bid] (100.000) for [h:strategy|chat]\r\nDead state behavior:\r\nExecutes: [h:action|ah bid] (100.000) for [h:strategy|chat]\r\nReaction behavior:\r\nExecutes: [h:action|ah bid] (100.000) for [h:strategy|chat]', '', '', '', '', '', '', '', '', '', 0),
